import React, { useState } from 'react';

function Home() {
  const [donations, setDonations] = useState([]);
  const [donation, setDonation] = useState({ description: '', image: null });

  const handleDescriptionChange = (event) => {
    setDonation({ ...donation, description: event.target.value });
  };

  const handleImageChange = (event) => {
    setDonation({ ...donation, image: event.target.files[0] });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setDonations([...donations, donation]);
    setDonation({ description: '', image: null }); // Reset form
  };

  return (
    <div>
      <h2>What would you like to donate?</h2>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Description of the item" 
          value={donation.description} 
          onChange={handleDescriptionChange} 
          required 
        />
        <input 
          type="file" 
          onChange={handleImageChange} 
          required 
        />
        <button type="submit">Donate</button>
      </form>
      <div>
        <h3>Donations</h3>
        <ul>
          {donations.map((item, index) => (
            <li key={index}>
              {item.image && <img src={URL.createObjectURL(item.image)} alt="Donation" style={{ width: "100px", height: "100px" }} />}
              <p>{item.description}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Home;
