import React, { useState } from 'react';
import '../CSS/Message.css'; // Make sure you have the CSS file for styling

const Messages = () => {
  // State for handling messages
  const [currentThread, setCurrentThread] = useState(null);
  const [newMessage, setNewMessage] = useState('');
  // Mockup threads data
  const [threads, setThreads] = useState([
    { id: 1, name: 'Alice Smith', messages: ['Hi there!'] },
    // ...other threads
  ]);

  // Handles clicking on a thread to view messages
  const handleThreadClick = (thread) => {
    setCurrentThread(thread);
  };

  // Handles sending a message
  const handleSendMessage = () => {
    if (!newMessage.trim()) return;
    // Add sending message logic here
    // For now, we'll just update the local state
    const updatedThreads = threads.map((thread) => {
      if (thread.id === currentThread.id) {
        return {
          ...thread,
          messages: [...thread.messages, newMessage],
        };
      }
      return thread;
    });
    setThreads(updatedThreads);
    setNewMessage('');
  };
  return (
    <div className="messages-container">
      <div className="message-system">
        <div className="thread-list">
          {/* List of conversations */}
          {threads.map((thread) => (
            <div
              key={thread.id}
              className={`thread-item ${currentThread && currentThread.id === thread.id ? 'active-thread' : ''}`}
              onClick={() => handleThreadClick(thread)}
            >
              <div className="thread-details">
                <div className="thread-name">{thread.name}</div>
                <div className="thread-preview">{thread.messages[thread.messages.length - 1]}</div>
              </div>
            </div>
          ))}
        </div>
        <div className="message-area">
          {/* Message view for the selected conversation */}
          {currentThread ? (
            <>
              <div className="message-list">
                {/* List of messages in the conversation */}
                {currentThread.messages.map((msg, index) => (
                  <div key={index} className="message-bubble">
                    {msg}
                  </div>
                ))}
              </div>
              <div className="message-input-area">
                {/* Input area for typing new messages */}
                <input
                  type="text"
                  className="message-input"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Type a message..."
                />
                <button className="send-button" onClick={handleSendMessage}>Send</button>
              </div>
            </>
          ) : (
            <div className="no-thread-selected">
              {/* Prompt when no conversation is selected */}
              Select a conversation to start messaging.
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Messages;
