import React, { useState } from 'react';

function NewItemForm({ onNewItem }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();
    onNewItem({ name, description, image });
    setName('');
    setDescription('');
    setImage(null);
    setImagePreview(null);
  };

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    setImage(file);
    setImagePreview(URL.createObjectURL(file));
  };

  return (
    <div style={{ margin: 'auto', width: '50%', padding: '10px' }}>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column' }}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Product Name"
          required
          style={{ margin: '5px 0', padding: '10px' }}
        />
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          required
          style={{ margin: '5px 0', padding: '10px' }}
        />
        <input
          type="file"
          onChange={handleImageChange}
          required
          style={{ margin: '5px 0', padding: '10px' }}
        />
        <button type="submit" style={{ margin: '5px 0', padding: '10px', backgroundColor: '#4CAF50', color: 'white' }}>
          Post Item
        </button>
      </form>
      {imagePreview && (
        <img
          src={imagePreview}
          alt="Preview"
          style={{ width: '100px', height: '100px', margin: '10px 0' }}
        />
      )}
    </div>
  );
}

export default NewItemForm;
