import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Assuming you're still using React Router for navigation
import '../CSS/SignUp.css'; // Custom CSS for styling

function SignUp() {
  const [user, setUser] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
  });

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(user);
    // Here you would usually send the data to the server for verification
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser(prevState => ({ ...prevState, [name]: value }));
  };

  return (
    <div className="signup-container">
      <form className="signup-form" onSubmit={handleSubmit}>
        <h2>Sign Up</h2>
        <div className="form-group">
          <input
            type="text"
            name="firstName"
            placeholder="First Name"
            required
            value={user.firstName}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <input
            type="text"
            name="lastName"
            placeholder="Last Name"
            required
            value={user.lastName}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <input
            type="email"
            name="email"
            placeholder="Email Address"
            required
            value={user.email}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            name="password"
            placeholder="Password"
            required
            value={user.password}
            onChange={handleChange}
          />
        </div>
        <button type="submit" className="submit-btn">Sign Up</button>
        <div className="signin-link">
          Already have an account? <Link to="/Signin">Sign in</Link>
        </div>
      </form>
    </div>
  );
}

export default SignUp;
