import React, { useState, useEffect } from 'react';
import profilePicPlaceholder from '../images/image1.jpg';
import '../CSS/Profile.css';

const Profile = () => {
  const [profile, setProfile] = useState({
    firstname: '',
    lastname: '',
    email: '',
    address: '',
    phone: '',
    profilePic: profilePicPlaceholder,
  });
  



  return (
    <article className="profile-container">
      <header className="profile-header">
        <img src={profile.profilePic} alt="Profile" className="profile-picture" />
        <input
          id="profile-picture-upload"
          type="file"
          onChange={handleImageChange}
          style={{ display: 'none' }}
          accept="image/*"
        />
        <label htmlFor="profile-picture-upload" className="edit-picture-button">
          Edit Profile Picture
        </label>
      </header>
      <div className="profile-info">
        <label htmlFor="firstname">First Name</label>
        <input
          id="firstname"
          name="firstname"
          value={profile.firstname}
          onChange={handleChange}
        />
        <label htmlFor="lastname">Last Name</label>
        <input
          id="lastname"
          name="lastname"
          value={profile.lastname}
          onChange={handleChange}
        />
        <label htmlFor="email">E-mail</label>
        <input
          id="email"
          name="email"
          value={profile.email}
          onChange={handleChange}
        />
        <label htmlFor="address">Address</label>
        <input
          id="address"
          name="address"
          value={profile.address}
          onChange={handleChange}
        />
        <label htmlFor="phone">Phone No</label>
        <input
          id="phone"
          name="phone"
          value={profile.phone}
          onChange={handleChange}
        />
        <button className="save-button" onClick={handleSave}>
          Save
        </button>
      </div>
    </article>
  );
};

export default Profile;
