import React, { useState, useEffect } from 'react';
import profilePicPlaceholder from '../images/image1.jpg'; // Default image placeholder

import '../CSS/Profile.css'; // Make sure to link your CSS file

const Profile = () => {
  const [profile, setProfile] = useState({
    firstname: '',
    lastname: '',
    email: '',
    address: '',
    phone: '',
    profilePic: profilePicPlaceholder, // Keep the placeholder till real image loads
  });

  useEffect(() => {
    // Function to fetch the user profile data
    const fetchProfileData = async () => {
      try {
        // Replace this URL with the actual endpoint from your backend
        const response = await fetch('/api/user/profile');
        const data = await response.json();
        if (response.ok) {
          setProfile({ ...profile, ...data });
        } else {
          // Handle errors
          console.error('Error fetching profile data:', data);
        }
      } catch (error) {
        // Handle network errors
        console.error('Network error:', error);
      }
    };

    fetchProfileData();
  }, []);
  const handleSave = async () => {
    // Form submission logic goes here
  };

  const handleImageChange = (event) => {
    if (event.target.files && event.target.files[0]) {
      const fileReader = new FileReader();
      fileReader.onload = function (e) {
        setProfile({ ...profile, profilePic: e.target.result });
      };
      fileReader.readAsDataURL(event.target.files[0]);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setProfile({ ...profile, [name]: value });
  };

  return (
    <article className="profile-container">
      <header className="profile-header">
        <img src={profile.profilePic} alt="Profile" className="profile-picture" />
        <input
          id="profile-picture-upload"
          type="file"
          onChange={handleImageChange}
          style={{ display: 'none' }}
          accept="image/*"
        />
        <label htmlFor="profile-picture-upload" className="edit-picture-button">
          Edit Profile Picture
        </label>
      </header>
      <div className="profile-info">
        <label htmlFor="firstname">First Name</label>
        <input
          id="firstname"
          name="firstname"
          value={profile.firstname}
          onChange={handleChange}
        />
        <label htmlFor="lastname">Last Name</label>
        <input
          id="lastname"
          name="lastname"
          value={profile.lastname}
          onChange={handleChange}
        />
        <label htmlFor="email">E-mail</label>
        <input
          id="email"
          name="email"
          value={profile.email}
          onChange={handleChange}
        />
        <label htmlFor="address">Address</label>
        <input
          id="address"
          name="address"
          value={profile.address}
          onChange={handleChange}
        />
        <label htmlFor="phone">Phone No</label>
        <input
          id="phone"
          name="phone"
          value={profile.phone}
          onChange={handleChange}
        />
        <button className="save-button" onClick={handleSave}>
          Save
        </button>
      </div>
    </article>
  );
};

export default Profile;
