import React, { useState } from 'react';
import image1 from '../images/image1.jpeg'; // Make sure this path is correct
import '../CSS/Home.css';

const Home = () => {
  const [postText, setPostText] = useState('');
  const [image, setImage] = useState(null);

  const user = {
    name: 'John Doe',
    profilePic: image1,
  };

  // Function definitions for handleTextChange, handleImageChange, handleSubmit...

  return (
    <div className="post-container">
      <div className="user-profile">
        <img src={user.profilePic} alt="Profile" className="profile-pic" />
        <span className="user-name">{user.name}</span>
      </div>
      <div className="post-area">
        <input
          type="text"
          placeholder="You want to donate some food today?"
          value={postText}
          onChange={handleTextChange}
          className="post-input"
        />
        <input
          type="file"
          onChange={handleImageChange}
          className="file-input"
        />
        <button type="submit" onClick={handleSubmit} className="post-button">
          Post
        </button>
      </div>
    </div>
  );
};

export default Home;
