import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import { Box, Button, TextField, Typography, Link } from '@mui/material';
import '../CSS/Home.css'; // Make sure your CSS styles are correctly applied

function Home() {
  const [donations, setDonations] = useState([]);
  const [newDonation, setNewDonation] = useState({
    description: '',
    image: null,
  });

  const handleDescriptionChange = (e) => {
    setNewDonation({ ...newDonation, description: e.target.value });
  };

  const handleImageChange = (e) => {
    setNewDonation({ ...newDonation, image: e.target.files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!newDonation.description || !newDonation.image) return;
    setDonations([...donations, newDonation]);
    setNewDonation({ description: '', image: null });
  };

  return (
    <Box className="main-container">
      <Typography variant="h4" component="h1">What would you like to donate?</Typography>
      <Box component="form" onSubmit={handleSubmit} className="form-container">
        <TextField
          required
          label="Description"
          variant="outlined"
          fullWidth
          value={newDonation.description}
          onChange={handleDescriptionChange}
          sx={{ mb: 2 }}
        />
        <Button variant="contained" component="label" sx={{ mb: 2 }}>
          Upload Image
          <input
            type="file"
            hidden
            onChange={handleImageChange}
          />
        </Button>
        <Button type="submit" variant="contained" fullWidth>Donate</Button>
      </Box>
      <Box className="donations-list">
        {donations.map((item, index) => (
          <article key={index} className="donation-item">
            {item.image && (
              <img
                src={URL.createObjectURL(item.image)}
                alt="Donation"
                style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '4px' }}
              />
            )}
            <Typography>{item.description}</Typography>
          </article>
        ))}
      </Box>
      <Typography sx={{ textAlign: 'center', mt: 4 }}>
        Looking for more ways to help?{' '}
        <Link component={RouterLink} to="/SignUp">Join us</Link>.
      </Typography>
    </Box>
  );
}

export default Home;
