import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined'
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Link as RouterLink } from 'react-router-dom';
import Typography from '@mui/material/Typography';

// TODO remove, this demo shouldn't need to reset the theme.

const defaultTheme = createTheme();

export default function SignIn() {
  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    console.log({
      email: data.get('email'),
      password: data.get('password'),
    });
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <Container component="main" maxWidth="sm">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
         <Avatar sx={{ m: 1, bgcolor: 'secondary.main', width: 180, height: 180 }}>
  <LockOutlinedIcon sx={{ fontSize: 80 }} /> {/* Custom font size */}
</Avatar>

          <Typography component="h1" variant="h5" style={{ fontSize: '10rem' }}> 
  Sign in
</Typography>

<Box component="form" onSubmit={handleSubmit} noValidate xl={{ mt: 1 }}>
  <TextField
    margin="normal"
    required
    fullWidth
    id="email"
    label="Email Address"
    name="email"
    autoComplete="email"
    autoFocus
    InputProps={{
      style: {
        fontSize: '3rem', // Adjust input text font size
        padding: '10px 14px', // Increase padding inside the text field
      },
    }}
    InputLabelProps={{
      style: {
        fontSize: '3rem', // Adjust label font size
      },
    }}
  
  />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
            />
            <FormControlLabel
              control={<Checkbox value="remember" color="primary" />}
              label="Remember me"
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Sign In
            </Button>
            <Grid container>
  <Grid item xs>
    {/* Use RouterLink here for Forgot password */}
    <Link component={RouterLink} to="/ForgotPassword" variant="body2">
      Forgot password?
    </Link>
  </Grid>
  <Grid item>
    {/* Use RouterLink here for Sign Up */}
    <Link component={RouterLink} to="/SignUp" variant="body2">
      {"Don't have an account? Sign Up"}
    </Link>
  </Grid>
</Grid>
  

          </Box>
        </Box>
      </Container>
    </ThemeProvider>
  );
}
