import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button'; // Import Button for consistent styling
import Typography from '@mui/material/Typography'; // For text styling
import Link from '@mui/material/Link'; // For the "Sign Up" link
import { Link as RouterLink } from 'react-router-dom'; // For SPA navigation without page reload
import '../CSS/SignIn.css'; // Assuming your CSS styles remain the same

function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('User Email:', email);
    console.log('Password:', password);
    // Here you would usually send the data to the server for verification
  };

  return (
    <div className="signin-container">
      <form className="signin-form" onSubmit={handleSubmit}>
        <h2>Sign In</h2>
        <div className="form-group">
          <TextField
            required
            id="outlined-required-email"
            label="Email Address"
            variant="outlined"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            fullWidth // Make TextField full width
          />
        </div>
        <div className="form-group">
          <TextField
            required
            id="outlined-password-input"
            label="Password"
            type="password"
            variant="outlined"
            autoComplete="current-password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            fullWidth // Make TextField full width
          />
        </div>
        {/* Added section for sign-up prompt */}
        <Typography variant="body2" style={{ textAlign: 'center', margin: '20px 0' }}>
          Don't have an account?{' '}
          <Link component={RouterLink} to="/SignUp" style={{ textDecoration: 'none' }}>
            Sign Up
          </Link>
        </Typography>
        <div className="form-actions">
          <Button type="submit" variant="contained" fullWidth>Sign In</Button>
        </div>
      </form>
    </div>
  );
}

export default SignIn;
