import React from 'react';
import '../CSS/Profile.css'; // Make sure to link your CSS file

const Profile = () => {
  // Placeholder functions for handling events
  const handleEditAvatar = () => { console.log('Edit avatar clicked'); };
  const handleToggleSwitch = () => { console.log('Toggle switch changed'); };

  return (
    <div className="profile-container">
      <header className="profile-header">
        <h1>Edit Profile</h1>
      </header>
      <section className="profile-picture-section">
        <img src="path-to-profile-pic.jpg" alt="Profile avatar" className="profile-picture" />
        <button onClick={handleEditAvatar}>Edit picture or avatar</button>
      </section>
      <div className="profile-info">
        <label>Name</label>
        <div>Abu Ehsan</div>
        <label>Username</label>
        <div>abuehsan5</div>
        {/* Repeat for other fields */}
      </div>
      <div className="profile-settings">
        <div className="profile-option" onClick={() => {}}>
          Show Threads badge
          <label className="switch">
            <input type="checkbox" onChange={handleToggleSwitch} />
            <span className="slider round"></span>
          </label>
        </div>
        {/* Repeat for other settings */}
      </div>
    </div>
  );
};

export default Profile;
