import React, { useState, useEffect } from 'react'; // Add useEffect to the import
import axios from 'axios'; // Import axios to make API calls
import imagePlaceholder from '../images/image1.jpg'; // Import a placeholder image
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import MessageIcon from '@mui/icons-material/Message';
import '../CSS/Home.css';

const Home = () => {
  // Removed postText state since you're not using it, clear the warning
  const [image, setImage] = useState(null);
  const [productName, setProductName] = useState('');
  const [description, setDescription] = useState('');
  const [quantity, setQuantity] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [items, setItems] = useState([]);
  const [unit, setUnit] = useState('');
  const [category, setCategory] = useState('');
  const [user, setUser] = useState({ name: '', profilePic: imagePlaceholder });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/user/current', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        if (!response.ok) {
          throw new Error('Network response was not ok.');
        }
        const data = await response.json();
        setUser({
          name: data.name,
          profilePic: data.profilePic || imagePlaceholder
        });
      } catch (error) {
        console.error('Error fetching user data:', error);
        // Handle error here
      }
    };
  
    fetchUserData();
  }, []);
  

  
  const toggleForm = () => setShowForm(!showForm);

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const newItem = {
      userName: user.name,
      productName,
      description,
      quantity,
      unit,
      category,
      imageUrl: URL.createObjectURL(image),
    };
    setItems([...items, newItem]);
    
    setProductName('');
    setDescription('');
    setQuantity('');
    setUnit('');
    setCategory('');
    setImage(null);
    setShowForm(false);
  };

  return (
    <div>
      <div className="post-container">
        <div className="user-info">
          <img src={user.profilePic} alt={user.name} className="profile-pic" />
          <p className="user-name">{user.name}</p>
        </div>
        <div className="post-prompt">
          <p>What do you want to donate?</p>
        </div>
        <div className="add-prompt" onClick={toggleForm}>
          <p>Click here to add</p>
        </div>
      </div>

      {showForm && (
        <article className="donation-form">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Product Name"
              value={productName}
              onChange={(e) => setProductName(e.target.value)}
              required
            />
            <textarea
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
             <input
              type="number"
              placeholder="Quantity"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              required
            />
            <select
              value={unit}
              onChange={(e) => setUnit(e.target.value)}
              required
            >
              <option value="">Select Unit</option>
              <option value="kg">Kg</option>
              <option value="pcs">Pieces</option>
              {/* ... other unit options */}
            </select>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            >
              <option value="">Select Category</option>
              <option value="vegan">Vegan</option>
              <option value="meat">Meat</option>
              <option value="halal">Halal</option>
            </select>
            <input
              type="file"
              onChange={handleImageChange}
              required
            />
            <button type="submit">Upload</button>
          </form>
        </article>
      )}

      <section className="feed">
        {items.map((item, index) => (
          <article key={index} className="feed-item">
            <div className="feed-item-image">
              <img src={item.imageUrl} alt={item.productName} />
            </div>
            <div className="feed-item-info">
              <h1>User: {item.userName}</h1>
              <h3 className="color">Prdouct Name: {item.productName}</h3>
              <p>Description: {item.description}</p>
              <p>Quantity: {item.quantity}</p>
              <p>Unit: {item.unit}</p>
              <p>Category: {item.category}</p>
              <div className="feed-item-actions">
              <button>< FavoriteBorderIcon className="action-icon" />Love</button>
              <button>< MessageIcon className="action-icon" />Message</button>
              </div>
            </div>
          </article>
        ))}
      </section>
    </div>
  );
};

export default Home;
