import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Assuming React Router for SPA navigation
import Button from '@mui/material/Button'; // Import for Material-UI button
import TextField from '@mui/material/TextField'; // Material-UI TextField for input fields
import Typography from '@mui/material/Typography'; // For styled text
import Box from '@mui/material/Box'; // Container component from Material-UI
import { Link as RouterLink } from 'react-router-dom'; // For internal navigation without refreshing the page
import '../CSS/SignUp.css'; // Assuming you might still have some global or specific CSS styles

function SignUp() {
  const [user, setUser] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
  });

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(user);
    // Implement your sign-up logic here, e.g., sending data to a server
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setUser({ ...user, [name]: value });
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundColor: '#f7f8fa',
      }}
    >
      <Box
        component="form"
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          '& .MuiTextField-root': { m: 1, width: '25ch' },
        }}
        noValidate
        autoComplete="off"
        onSubmit={handleSubmit}
      >
        <Typography component="h1" variant="h5">
          Sign Up
        </Typography>
        <TextField
          required
          id="firstName"
          label="First Name"
          name="firstName"
          value={user.firstName}
          onChange={handleChange}
          autoComplete="given-name"
        />
        <TextField
          required
          id="lastName"
          label="Last Name"
          name="lastName"
          value={user.lastName}
          onChange={handleChange}
          autoComplete="family-name"
        />
        <TextField
          required
          id="email"
          label="Email Address"
          name="email"
          type="email"
          value={user.email}
          onChange={handleChange}
          autoComplete="email"
        />
        <TextField
          required
          id="password"
          label="Password"
          name="password"
          type="password"
          value={user.password}
          onChange={handleChange}
          autoComplete="new-password"
        />
        <Button
          type="submit"
          variant="contained"
          sx={{ mt: 2, mb: 2 }}
        >
          Sign Up
        </Button>
        <Typography variant="body2">
          Already have an account?{' '}
          <Link component={RouterLink} to="/signin">
            Sign in
          </Link>
        </Typography>
      </Box>
    </Box>
  );
}

export default SignUp;
