import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import { Box, Button, TextField, Typography, Link } from '@mui/material';
import backgroundImage from '../images/signin.jpg';
import '../CSS/SignIn.css'; 
function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('User Email:', email);
    console.log('Password:', password);
    // Here, handle the sign-in logic, typically verifying credentials against a server
  };

  return (
    <div class="signin-intro">
  <h2>Join the movement to end food wasteâ€”one donation, one meal, one reward at a time.</h2>


    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundColor: '#f7f8fa',
        backgroundImage: `url(${backgroundImage})`,
        backgroundPosition: 'center', // Centers the background image
        backgroundRepeat: 'no-repeat', // Prevents the background image from repeating
        backgroundSize: 'cover',
      }}
    >
      <Box
        component="form"
        onSubmit={handleSubmit}
        noValidate
        autoComplete="off"
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          padding: '30px',
          backgroundColor: '#ffffff',
          borderRadius: '12px',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
          width: '100%',
          maxWidth: '250px',
          marginBottom: '50px',
        }}
      >
        <Typography component="h1" variant="h5" sx={{ mb: 2 }}>
          Sign In
        </Typography>
        <TextField
          required
          id="email"
          name="email"
          label="Email Address"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          fullWidth
          sx={{ mb: 2 }}
        />
        <TextField
          required
          id="password"
          name="password"
          label="Password"
          type="password"
          autoComplete="current-password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          fullWidth
          sx={{ mb: 2 }}
        />
        {/* Adjust the button's width here */}
        <Button 
          variant="contained" 
          type="submit" 
          sx={{ 
            mb: 2, 
            width: '40%', // Adjusts the button width to 75% of its container
          }}
        >
          Sign In
        </Button>
        <Typography sx={{ textAlign: 'center' }}>
          Don't have an account?{' '}
          <Link component={RouterLink} to="/SignUp" sx={{ textDecoration: 'none' }}>
            Sign Up
          </Link>
        </Typography>
        <Typography sx={{ textAlign: 'Center' }}>
          Forgot Password?{' '}
          <Link component={RouterLink} to="/ForgotPassword" sx={{ textDecoration: 'none' }}>
            Reset 
          </Link>
        </Typography>
      </Box>
    </Box>
    </div>
  );
}

export default SignIn;
