import React, { useState } from 'react';
import image1 from '../images/image1.jpeg'; // Import the profile image
import '../CSS/Home.css';

const Home = () => {
  const [postText, setPostText] = useState('');
  const [image, setImage] = useState(null);

  const user = {
    name: 'John Doe',
    profilePic: image1, // Use the imported image here
  };

  // ... (rest of your component)

  return (
    <div className="post-container">
      <div className="user-info">
        <img src={user.profilePic} alt={user.name} className="profile-pic" />
        <p className="user-name">{user.name}</p>
      </div>
      <div className="post-form">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="You want to donate some food today?"
            value={postText}
            onChange={handleTextChange}
            className="post-input"
          />
          <input
            type="file"
            onChange={handleImageChange}
            className="post-image-upload"
          />
          <button type="submit" className="submit-post-button">
            Post
          </button>
        </form>
      </div>
    </div>
  );
};

export default Home;
