import React, { useState } from 'react';

function Home() {
  // State to hold the list of all posts
  const [posts, setPosts] = useState([]);
  // State to hold the current input for a new post
  const [newPost, setNewPost] = useState("");

  // Handler for adding a new post
  const addPost = () => {
    if (!newPost) return; // Don't add empty posts
    // Create a new post object
    const post = {
      id: posts.length + 1, // Simple unique id
      content: newPost, // Post content from the state
    };
    setPosts([...posts, post]); // Add new post to the list of posts
    setNewPost(""); // Reset the input field after adding a post
  };

  return (
    <div className="App">
      <h1>Home Page</h1>
      {/* New Post Input */}
      <div>
        <input
          type="text"
          value={newPost}
          onChange={(e) => setNewPost(e.target.value)}
          placeholder="What's on your mind?"
        />
        <button onClick={addPost}>Post</button>
      </div>
      {/* List of Posts */}
      <div>
        {posts.map((post) => (
          <div key={post.id} className="post">
            {post.content}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
