import React, { useState } from 'react';
import image1 from '../images/image1.jpeg';
import '../CSS/Home.css';

const Home = () => {
  const [postText, setPostText] = useState('');
  const [image, setImage] = useState(null);

  // Mock user data - replace with actual data as needed
  const user = {
    name: 'John Doe', // The user's name
    profilePic: image1, // The path to the user's profile picture
  };
  const handleTextChange = (event) => {
    setPostText(event.target.value);
  };

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Process and send data to the server
    console.log(postText);
    if (image) {
      console.log(image.name);
    }
    // Clear the form
    setPostText('');
    setImage(null);
  };

  return (
    <div className="post-container">
      <div className="user-info">
        {/* Ensure the src attribute points to the correct imported image path */}
        <img src={user.profilePic} alt={user.name} className="profile-pic" />
        {/* Make sure the user's name is only included once */}
        <p className="user-name">{user.name}</p>
      </div>
      <div className="post-form">
        {/* ... */}
      </div>
    </div>
  );
};

export default Home;
