import React from 'react';
import '../CSS/Rewards.css'; // Your CSS file for styling

const rewards = [
  // ... your rewards data
];

const RewardCard = ({ reward }) => {
  return (
    <div className="reward-card">
      <img src={reward.image} alt={reward.name} className="reward-image" />
      <div className="reward-info">
        <h3>{reward.name}</h3>
        <p>{reward.description}</p>
        <div className="reward-progress">
          <progress value={reward.currentPoints} max={reward.requiredPoints}></progress>
          <span>{reward.currentPoints}/{reward.requiredPoints} Points</span>
        </div>
        <button className="btn-claim">Claim Reward</button>
      </div>
    </div>
  );
};

const Rewards = () => {
  return (
    <div className="rewards-container">
      {rewards.map(reward => (
        <RewardCard key={reward.id} reward={reward} />
      ))}
    </div>
  );
};

export default Rewards;
