import React, { useState } from 'react';
import NewItemForm from '../Components/NewItemForm';

function Home() {
  const [items, setItems] = useState([]);

  const addNewItem = (item) => {
    setItems([...items, { ...item, id: Date.now() }]); // Assign a unique id for key prop purposes
  };

  return (
    <div style={{ padding: '20px' }}>
      <NewItemForm onNewItem={addNewItem} />
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px', marginTop: '20px' }}>
        {items.map((item) => (
          <div key={item.id} style={{ border: '1px solid #ddd', borderRadius: '8px', padding: '10px', backgroundColor: '#f9f9f9' }}>
            <h3>{item.name}</h3>
            <p>{item.description}</p>
            {item.image && <img src={item.image} alt={item.name} style={{ width: '100%', height: 'auto', marginBottom: '10px' }} />}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
