import React, { useState } from 'react';

function NewItemForm({ onNewItem }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Assuming onNewItem is a prop function that lifts state up to the parent component
    onNewItem({ name, description, image });
    setName('');
    setDescription('');
    setImage(null);
  };

  const handleImageChange = (event) => {
    setImage(URL.createObjectURL(event.target.files[0]));
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Product Name"
          required
        />
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          required
        />
        <input
          type="file"
          onChange={handleImageChange}
          required
        />
        <button type="submit">Post Item</button>
      </form>
      {image && <img src={image} alt="Preview" style={{ width: '100px', height: '100px' }} />}
    </div>
  );
}

export default NewItemForm;
