import React, { useState } from 'react';

const HomePage = () => {
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission, perhaps sending the data to a server
  };

  return (
    <div>
      <nav>
        {/* Navigation bar content here */}
      </nav>
      <div style={{ display: 'flex', marginTop: '20px' }}>
        <aside style={{ flex: '1' }}>
          {/* User profile section */}
          <img src="path_to_profile_picture.jpg" alt="Profile" />
          <p>Username</p>
        </aside>
        <main style={{ flex: '3' }}>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="foodDescription">Food Description</label>
              <textarea
                id="foodDescription"
                value={description}
                onChange={handleDescriptionChange}
              />
            </div>
            <div>
              <label htmlFor="foodImage">Upload Image</label>
              <input
                type="file"
                id="foodImage"
                accept="image/*"
                onChange={handleImageChange}
              />
            </div>
            <button type="submit">Submit</button>
          </form>
        </main>
      </div>
    </div>
  );
};

export default HomePage;
