import React, { useState } from 'react';
import { Box, Button, TextField, Typography, Link } from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';
import '../CSS/Home.css';  // Make sure your CSS styles are correctly applied

function Home() {
  const [showForm, setShowForm] = useState(false);
  const [donations, setDonations] = useState([
    // Predefined donations
    { id: 1, description: 'Potato', image: 'C:\Users\amimu\OneDrive\Desktop\FYP\zero-food-waste\public\image.jpg' },
    { id: 2, description: 'Tomato', image: 'C:\Users\amimu\OneDrive\Desktop\FYP\zero-food-waste\public\image1.jpg' }
  ]);
  const [newDonation, setNewDonation] = useState({
    description: '',
    image: null,
  });

  const handleDescriptionChange = (e) => {
    setNewDonation({ ...newDonation, description: e.target.value });
  };

  const handleImageChange = (e) => {
    setNewDonation({ ...newDonation, image: e.target.files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!newDonation.description || !newDonation.image) return;
    setDonations([...donations, { ...newDonation, id: donations.length + 1 }]);
    setNewDonation({ description: '', image: null });
    setShowForm(false); // Hide form again after submission
  };

  return (
    <Box className="main-container">
      <Typography variant="h4" component="h1">Community Donations</Typography>
      {!showForm && (
        <Button variant="outlined" onClick={() => setShowForm(true)}>
          What would you like to donate?
        </Button>
      )}
      {showForm && (
        <Box component="form" onSubmit={handleSubmit} className="form-container">
          <TextField
            required
            label="Description"
            variant="outlined"
            fullWidth
            value={newDonation.description}
            onChange={handleDescriptionChange}
            sx={{ mb: 2 }}
          />
          <Button variant="contained" component="label" sx={{ mb: 2 }}>
            Upload Image
            <input
              type="file"
              hidden
              onChange={handleImageChange}
            />
          </Button>
          <Button type="submit" variant="contained" fullWidth>Donate</Button>
        </Box>
      )}
      <Box className="donations-list">
        {donations.map((item) => (
          <article key={item.id} className="donation-item">
            <img src={item.image} alt="Donation" />
            <Typography>{item.description}</Typography>
          </article>
        ))}
      </Box>
    </Box>
  );
}

export default Home;
