import React, { useState } from 'react';

// This could be your main App or a component in your application
function Message() {
  const [selectedConversation, setSelectedConversation] = useState(null);

  // Placeholder data for the list of conversations
  const conversations = [
    { id: 1, name: 'Mert', lastMessage: 'My address is e16 3np.' },
    { id: 2, name: 'Jack', lastMessage: 'Pick up time 5-5:30 pm.' },
    { id: 3, name: 'Dani', lastMessage: 'Hi' },
    // ... other conversations
  ];

  // Placeholder data for messages in a conversation
  const messages = [
    { id: 1, text: 'Hi, that food is still available ?', sender: 'Mert' },
    { id: 2, text: 'Yes, you can collect from my home.', sender: 'You' },
    { id: 3, text: 'Okay, what is your address?', sender: 'Mert' },
    { id: 4, text: 'E16, 3NP. Collection time 5:30 pm.', sender: 'You' },
    // ... other messages
  ];

  return (
    <div>
      <ConversationList 
        conversations={conversations} 
        onSelectConversation={setSelectedConversation} 
      />
      {selectedConversation && (
        <MessageView 
          conversation={selectedConversation} 
          messages={messages} 
        />
      )}
    </div>
  );
}

// List of conversations
function ConversationList({ conversations, onSelectConversation }) {
  return (
    <div className="conversation-list">
      {conversations.map((conversation) => (
        <div 
          key={conversation.id} 
          className="conversation" 
          onClick={() => onSelectConversation(conversation)}
        >
          <div className="conversation-info">
            <div className="conversation-name">{conversation.name}</div>
            <div className="conversation-last-message">{conversation.lastMessage}</div>
          </div>
        </div>
      ))}
    </div>
  );
}

// Individual message view
function MessageView({ conversation, messages }) {
  return (
    <div className="message-view">
      <div className="conversation-title">{conversation.name}</div>
      <div className="messages">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.sender === 'You' ? 'sent' : 'received'}`}>
            <div className="message-text">{message.text}</div>
          </div>
        ))}
      </div>
      <div className="message-input">
        <input type="text" placeholder="Type new message here" />
        <button>Send</button>
      </div>
    </div>
  );
}

export default Message;
