import './App.css';
import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './Pages/Home';
import About from './Pages/About';
import Profile from './Pages/Profile';
import Navbar from './Components/Navbar';
import Tandc from './Pages/Tandc';
import Contact from './Pages/Contact';
import Signin from './Pages/Signin';
import Sidebar from './Components/Sidebar';
import SignUp from './Pages/SignUp';
import ForgotPassword from './Pages/ForgotPassword';
import Message from './Pages/Message';
import Notifications from './Pages/Notifications';
import Rewards from './Pages/Rewards';
function App() {
  // State for points and worth, assuming they start at 0 and are updated somehow
  const [points, setPoints] = useState(0);
  const [worth, setWorth] = useState(0);

  // Example function that updates points and worth, perhaps after fetching from an API
  const updateRewards = () => {
    // Fetch or calculate points and worth
    const newPoints = 120; // Example points
    const newWorth = 1;    // Example worth
    setPoints(newPoints);
    setWorth(newWorth);
  };
  return (
    <BrowserRouter>
    <Navbar />
    <Sidebar />
    <div style={{ marginLeft: '200px' }}>
      <Routes>
        <Route path="/Signin" element={<Signin />} />
        <Route path="/Home" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/profile" element={<Profile />} />
        <Route path="/Tandc" element={<Tandc />} />
        <Route path="/Contact" element={<Contact />} />
        <Route path="/Forgotpassword" element={<ForgotPassword />} />
        <Route path="/SignUp" element={<SignUp />} />
        <Route path="/Message" element={<Message />} />
        <Route path="/Notifications" element={<Notifications/>} />
        <Route path="/Rewards" element={<Rewards/>} />



      </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
