import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

function Signin() {
  // State for form values
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [rememberMe, setRememberMe] = useState(false);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({ username, password, rememberMe });
    // Add logic to handle sign in
  };

  return (
    <div className="wrapper">
      <form className="login box" onSubmit={handleSubmit}>
        <div className="login-header">
          <span>Login</span>
        </div>
        <div className="input_box">
          <input 
            type="text" 
            id="user" 
            className="input-field" 
            required 
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label htmlFor="user" className="label">Username</label>
          <i className="bx bx-user icon"></i>
        </div>
        <div className="input_box">
          <input 
            type="password" 
            id="pass" 
            className="input-field" 
            required 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label htmlFor="pass" className="label">Password</label>
          <i className="bx bx-lock-alt icon"></i>
        </div>
        <div className="remember-forgot">
          <div className="remember-me">
            <input 
              type="checkbox" 
              id="remember"
              checked={rememberMe}
              onChange={(e) => setRememberMe(e.target.checked)}
            />
            <label htmlFor="remember">Remember me</label>
          </div>
          <div className="forgot">
            <Link to="/forgot-password">Forgot password?</Link> {/* Use Link instead of <a> */}
          </div>
        </div>
        <div className="input_box">
          <input type="submit" className="input-submit" value="Login" />
        </div>
        <div className="register">
          <span>Don't have an account? <Link to="/register">Register</Link></span> {/* Use Link instead of <a> */}
        </div>
      </form>
    </div>
  );
}

export default Signin;
