import React, { useState } from 'react';
import '../CSS/Message.css'; // Make sure you have the CSS file for styling

const Messages = () => {
  // State for handling messages
  const [currentThread, setCurrentThread] = useState(null);
  const [newMessage, setNewMessage] = useState('');
  // Mockup threads data
  const [threads, setThreads] = useState([
    { id: 1, name: 'Alice Smith', messages: ['Hi there!'] },
    // ...other threads
  ]);

  // Handles clicking on a thread to view messages
  const handleThreadClick = (thread) => {
    setCurrentThread(thread);
  };

  // Handles sending a message
  const handleSendMessage = () => {
    if (!newMessage.trim()) return;
    // Add sending message logic here
    // For now, we'll just update the local state
    const updatedThreads = threads.map((thread) => {
      if (thread.id === currentThread.id) {
        return {
          ...thread,
          messages: [...thread.messages, newMessage],
        };
      }
      return thread;
    });
    setThreads(updatedThreads);
    setNewMessage('');
  };

  return (
    <div className="messages-layout">
      <aside className="sidebar">
        {/* Sidebar content */}
      </aside>
      <main className="messages-main">
        <div className="thread-list">
          {threads.map((thread) => (
            <div
              key={thread.id}
              className={`thread ${currentThread && currentThread.id === thread.id ? 'active' : ''}`}
              onClick={() => handleThreadClick(thread)}
            >
              {thread.name}
            </div>
          ))}
        </div>
        <div className="message-view">
          {currentThread ? (
            <>
              <div className="message-list">
                {currentThread.messages.map((msg, index) => (
                  <div key={index} className="message">
                    {msg}
                  </div>
                ))}
              </div>
              <div className="message-composer">
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Type a message..."
                />
                <button onClick={handleSendMessage}>Send</button>
              </div>
            </>
          ) : (
            <div className="no-thread-selected">
              Select a conversation to start messaging.
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default Messages;
