import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Link as RouterLink } from 'react-router-dom';


// TODO remove, this demo shouldn't need to reset the theme.

const defaultTheme = createTheme();

export default function SignUp() {
  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    console.log({
      email: data.get('email'),
      password: data.get('password'),
    });
  };

  return (
    <ThemeProvider theme={defaultTheme}>
     <Container component="main" maxWidth="md"> {/* Adjust maxWidth for overall form size */}
  <CssBaseline />
  <Box
    sx={{
      marginTop: 8,
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    }}
  >
    <Avatar sx={{ m: 1, bgcolor: 'secondary.main', width: 100, height: 100 }}> {/* Increase Avatar size */}
      <LockOutlinedIcon sx={{ fontSize: '3rem' }} /> {/* Increase Icon size */}
    </Avatar>
    <Typography component="h1" variant="h4" sx={{ fontSize: '3rem', margin: '20px 0' }}> {/* Increase Typography size */}
      Sign up
    </Typography>
    <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3, width: '100%', '.MuiTextField-root': { margin: '20px 0', '.MuiInputBase-input': { fontSize: '2rem' }, '.MuiInputLabel-root': { fontSize: '2rem' } }, '.MuiButton-root': { fontSize: '2rem', padding: '15px 0' }, '.MuiFormControlLabel-label': { fontSize: '1.5rem' } }}>
      <Grid container spacing={3}> {/* Increase spacing for larger elements */}
        {/* Adjust each Grid item similarly if needed */}
        <Grid item xs={12} sm={6}>
          <TextField
            autoComplete="given-name"
            name="firstName"
            required
            fullWidth
            id="firstName"
            label="First Name"
            autoFocus
            InputProps={{
              style: { fontSize: '1.5rem', padding: '20px 14px' }, // Bigger input text and padding
            }}
            InputLabelProps={{
              style: { fontSize: '1.5rem' }, // Bigger label
            }}
          />
        </Grid>
        {/* Repeat for other TextFields adjusting InputProps and InputLabelProps as above */}
        {/* ... */}
      </Grid>
      <Button
        type="submit"
        fullWidth
        variant="contained"
        sx={{ mt: 3, mb: 2, fontSize: '1.5rem', padding: '15px 0' }} // Larger button
      >
        Sign Up
      </Button>
      <Grid container justifyContent="flex-end">
        <Grid item>
          <Link component={RouterLink} to="/signin" variant="body2" sx={{ fontSize: '1.5rem' }}> {/* Larger link text */}
            Already have an account? Sign in
          </Link>
        </Grid>
      </Grid>
    </Box>
  </Box>
</Container>

    </ThemeProvider>
  );
}
