import React, { useState } from 'react';

function NewItemForm({ onNewItem }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();
    onNewItem({ name, description, image });
    setName('');
    setDescription('');
    setImage(null);
  };

  const handleImageChange = (event) => {
    setImage(URL.createObjectURL(event.target.files[0]));
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px' }}>
      <form onSubmit={handleSubmit} style={{ width: '100%', maxWidth: '500px' }}>
        <input
          style={{ width: '100%', padding: '10px', margin: '8px 0', boxSizing: 'border-box' }}
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Product Name"
          required
        />
        <textarea
          style={{ width: '100%', padding: '10px', margin: '8px 0', boxSizing: 'border-box' }}
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          required
        />
        <input
          style={{ display: 'block', margin: '8px 0' }}
          type="file"
          onChange={handleImageChange}
          required
        />
        <button type="submit" style={{ width: '100%', padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer', margin: '8px 0' }}>
          Post Item
        </button>
      </form>
      {image && <img src={image} alt="Preview" style={{ width: '200px', height: '200px', margin: '8px 0' }} />}
    </div>
  );
}

export default NewItemForm;
