import React, { useState } from 'react';
import image1 from '../images/image1.jpg';
import starIcon from '../images/star.png'; 
import likeIcon from '../images/like.png';
import MessageIcon from '../images/messenger.png';
import '../CSS/Home.css';

const Home = () => {
  const [postText, setPostText] = useState('');
  const [image, setImage] = useState(null);
  const [productName, setProductName] = useState('');
  const [description, setDescription] = useState('');
  const [quantity, setQuantity] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [items, setItems] = useState([]);
  const [unit, setUnit] = useState('');
  const [category, setCategory] = useState('');

  const user = {
    name: 'Talha',
    profilePic: image1,
  };

  const toggleForm = () => setShowForm(!showForm);

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const newItem = {
      userName: user.name,
      productName,
      description,
      quantity,
      unit,
      category,
      imageUrl: URL.createObjectURL(image),
    };
    setItems([...items, newItem]);
    
    setProductName('');
    setDescription('');
    setQuantity('');
    setUnit('');
    setCategory('');
    setImage(null);
    setShowForm(false);
  };

  return (
    <div>
      <div className="post-container">
        <div className="user-info">
          <img src={user.profilePic} alt={user.name} className="profile-pic" />
          <p className="user-name">{user.name}</p>
        </div>
        <div className="post-prompt">
          <p>What do you want to donate?</p>
        </div>
        <div className="add-prompt" onClick={toggleForm}>
          <p>Click here to add</p>
        </div>
      </div>

      {showForm && (
        <article className="donation-form">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Product Name"
              value={productName}
              onChange={(e) => setProductName(e.target.value)}
              required
            />
            <textarea
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
            <input
              type="number"
              placeholder="Quantity"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              required
            />
             <input
              type="number"
              placeholder="Quantity"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              required
            />
            <select
              value={unit}
              onChange={(e) => setUnit(e.target.value)}
              required
            >
              <option value="">Select Unit</option>
              <option value="kg">Kg</option>
              <option value="pcs">Pieces</option>
              {/* ... other unit options */}
            </select>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            >
              <option value="">Select Category</option>
              <option value="vegan">Vegan</option>
              <option value="meat">Meat</option>
              <option value="halal">Halal</option>
              {/* ... other category options */}
            </select>
            <input
              type="file"
              onChange={handleImageChange}
              required
            />
            <button type="submit">Upload</button>
          </form>
        </article>
      )}

      <section className="feed">
        {items.map((item, index) => (
          <article key={index} className="feed-item">
            <div className="feed-item-image">
              <img src={item.imageUrl} alt={item.productName} />
            </div>
            <div className="feed-item-info">
              <p>{item.userName}</p>
              <h3>{item.productName}</h3>
              <p>{item.description}</p>
              <p>Quantity: {item.quantity}</p>
              <div className="feed-item-actions">
                <button><img src={likeIcon} alt="Star" className="star-icon" />Like</button>
                <button><img src={MessageIcon} alt="Star" className="star-icon" />Message</button>
                <button><img src={starIcon} alt="Star" className="star-icon" />I'm interested</button>
              </div>
            </div>
          </article>
        ))}
      </section>
    </div>
  );
};

export default Home;
