import React, { useState } from 'react';
import image1 from '../images/image1.jpeg'; // Adjust the path as needed
import '../CSS/Home.css';

const Home = () => {
  const [postText, setPostText] = useState('');
  const [image, setImage] = useState(null);

  const user = {
    name: 'John Doe',
    profilePic: image1,
  };

  const handleTextChange = (event) => {
    setPostText(event.target.value);
  };

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // TODO: Process and send data to the server
    console.log(postText, image);
    // Reset the form fields
    setPostText('');
    setImage(null);
  };

  return (
    <div className="post-container">
      <div className="user-info">
        <img src={user.profilePic} alt={user.name} className="profile-pic" />
        <p className="user-name">{user.name}</p>
      </div>
      <div className="post-form">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="You want to donate some food today?"
            value={postText}
            onChange={handleTextChange}
            className="post-input"
          />
          <label className="post-image-upload" htmlFor="file-upload">
            Choose file
            <input
              id="file-upload"
              type="file"
              onChange={handleImageChange}
              style={{ display: 'none' }}
            />
          </label>
          <button type="submit" className="submit-post-button">Post</button>
        </form>
      </div>
    </div>
  );
};

export default Home;
