import React, { useState } from 'react';
import image1 from '../images/image1.jpeg';
import '../CSS/Home.css';

const Home = () => {
  const [postText, setPostText] = useState('');
  const [image, setImage] = useState(null);
  const [productName, setProductName] = useState('');
  const [description, setDescription] = useState('');
  const [quantity, setQuantity] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [items, setItems] = useState([]);

  const user = {
    name: 'John Doe',
    profilePic: image1,
  };

  const toggleForm = () => setShowForm(!showForm);

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // TODO: Process and send data to the server
    const newItem = {
      productName,
      description,
      quantity,
      imageUrl: URL.createObjectURL(image), // This is for preview purposes
    };
    setItems([...items, newItem]);
    
    // Clear the form fields
    setProductName('');
    setDescription('');
    setQuantity('');
    setImage(null);
    setShowForm(false);
  };

  return (
    <div>
      <div className="post-container" onClick={toggleForm}>
        <div className="user-info">
          <img src={user.profilePic} alt={user.name} className="profile-pic" />
          <p className="user-name">{user.name}</p>
        </div>
        <div className="post-prompt">
        <p>What do you want to donate?</p>
        <p>What do you want to donate?</p>
        </div>
      </div>

      {showForm && (
        <article className="donation-form">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Product Name"
              value={productName}
              onChange={(e) => setProductName(e.target.value)}
              required
            />
            <textarea
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
            <input
              type="number"
              placeholder="Quantity"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              required
            />
            <input
              type="file"
              onChange={handleImageChange}
              required
            />
            <button type="submit">Upload</button>
          </form>
        </article>
      )}

      <section className="feed">
        {items.map((item, index) => (
          <article key={index} className="feed-item">
            <img src={item.imageUrl} alt={item.productName} />
            <h3>{item.productName}</h3>
            <p>{item.description}</p>
            <p>Quantity: {item.quantity}</p>
          </article>
        ))}
      </section>
    </div>
  );
};

export default Home;
