import React, { useState } from 'react';
import profilePic from '../images/image1.jpg';

import '../CSS/Profile.css'; // Make sure to link your CSS file

const Profile = () => {
  const [profile, setProfile] = useState({
    name: 'Abu Ehsan',
    username: 'abuehsan5',
    email: 'abutalha2814@gmail.com',
    address: '10 Paul Close, E15 4QE',
    phone: '1234567890'
  });

  // Placeholder function for saving the edited data
  const handleSave = () => {
    console.log('Save clicked', profile);
  };

  // Update this function to handle profile picture changes
  const handleEditAvatar = () => {
    console.log('Edit avatar clicked');
  };

  // Update profile state as the user types into the form
  const handleChange = (event) => {
    const { name, value } = event.target;
    setProfile(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  return (
    <div className="profile-container">
      <header className="profile-header">
        <h1>Profile</h1>
        <img src={profilePic} alt="Profile" className="profile-picture" />

        <button onClick={handleEditAvatar}>Edit Profile picture</button>
      </header>
      <div className="profile-info">
        <label htmlFor="name">Name</label>
        <input id="name" name="name" value={profile.name} onChange={handleChange} />
       
      <label htmlFor="username">Username</label>
      <input
        id="username"
        name="username"
        value={profile.username}
        onChange={handleChange}
      />

      <label htmlFor="email">E-mail</label>
      <input
        id="email"
        name="email"
        value={profile.email}
        onChange={handleChange}
      />

      <label htmlFor="address">Address</label>
      <input
        id="address"
        name="address"
        value={profile.address}
        onChange={handleChange}
      />

      <label htmlFor="phone">Phone No</label>
      <input
        id="phone"
        name="phone"
        value={profile.phone}
        onChange={handleChange}
      />
      </div>
      <button className="save-button" onClick={handleSave}>Save</button>
    </div>
  );
};

export default Profile;
