import React, { useState } from 'react';

function NewItemForm({ onNewItem }) {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [location, setLocation] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();
    onNewItem({ name, description, image, location });
    // Reset form inputs if needed
  };

  const handleImageChange = (event) => {
    // Logic to handle image file
  };

  const handleLocationPick = (locationData) => {
    // Logic to handle location data from map picker
  };

  return (
    <div className="new-item-form-container">
      <form onSubmit={handleSubmit}>
        <div className="user-info">
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Your name"
            required
          />
          {/* Placeholder for user profile picture */}
        </div>
        <div className="product-info">
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Product description"
            required
          />
          <button type="button" onClick={() => { /* logic to handle image upload */ }}>
            Upload Image
          </button>
          {/* Display uploaded image if any */}
          {image && <img src={image} alt="Preview" />}
          <button type="button" onClick={() => { /* logic to handle map location picker */ }}>
            Pick Location
          </button>
          {/* Display picked location if any */}
          {location && <p>Location: {location.label}</p>}
        </div>
        <button type="submit">Post Item</button>
      </form>
    </div>
  );
}

export default NewItemForm;
