import React, { useState } from 'react';
import '../CSS/Message.css'; // make sure to create a CSS file with the name 'MessageApp.css'

const Message= () => {
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [conversations, setConversations] = useState([
    { id: 1, name: 'Mert', messages: ['Hi, that food is still available?'] },
    // ...other conversations
  ]);
  const [newMessage, setNewMessage] = useState('');

  const selectConversation = (conversation) => {
    setSelectedConversation(conversation);
  };

  const sendMessage = () => {
    if (newMessage.trim() === '' || !selectedConversation) return;
    // Here, you would handle sending the message to the server
    // and then fetching the updated conversation messages
    setNewMessage('');
  };

  return (
    <div className="message-app">
      <aside className="conversation-list">
        <div className="search-container">
          <input type="text" placeholder="Search" />
        </div>
        {conversations.map(convo => (
          <div key={convo.id} className="conversation" onClick={() => selectConversation(convo)}>
            <div className="conversation-name">{convo.name}</div>
            <div className="conversation-preview">{convo.messages[convo.messages.length - 1]}</div>
          </div>
        ))}
      </aside>
      <section className="conversation-details">
        {selectedConversation ? (
          <>
            <div className="chat-header">{selectedConversation.name}</div>
            <div className="messages">
              {selectedConversation.messages.map((msg, index) => (
                <div key={index} className="message">{msg}</div>
              ))}
            </div>
            <div className="message-input">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type new message here"
              />
              <button onClick={sendMessage}>Send</button>
            </div>
          </>
        ) : (
          <div className="no-conversation">Select a conversation</div>
        )}
      </section>
    </div>
  );
};

export default Message;
