import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link as RouterLink } from 'react-router-dom'; 
import { Box, Button, TextField, Typography, Link } from '@mui/material'; 
import '../CSS/SignUp.css'; 

function SignUp() {
  const [user, setUser] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
  });

  const navigate = useNavigate(); // This hook allows you to redirect the user after sign-up

  // Update the user state with new input
  const handleChange = (event) => {
    const { name, value } = event.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  // Send the user data to the backend upon form submission
  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevents the default form submission behavior

    try {
      // Replace 'http://localhost:5000/signup' with your actual backend endpoint
      const response = await axios.post('http://localhost:5000/signup', user);
      
      // If the signup is successful, you may want to redirect to the sign-in page or somewhere else
      navigate('/signin');
    } catch (error) {
      // Handle errors here, such as displaying a notification to the user
      console.error('There was an error!', error);
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '100vh',
        backgroundColor: '#f7f8fa',
      }}
    >
   
      <Box
        component="form"
        onSubmit={handleSubmit}
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          padding: '30px',
          backgroundColor: '#ffffff',
          borderRadius: '12px',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
          width: '100%',
          maxWidth: '250px', 
          marginBottom: '50px',
        }}
        noValidate
        autoComplete="off"
      >
        <Typography variant="h5" component="h2" sx={{ mb: 2 }}>
          Sign Up
        </Typography>
        <TextField
          required
          name="firstName"
          label="First Name"
          value={user.firstName}
          onChange={handleChange}
          fullWidth
          sx={{ mb: 2 }}
        />
        <TextField
          required
          name="lastName"
          label="Last Name"
          value={user.lastName}
          onChange={handleChange}
          fullWidth
          sx={{ mb: 2 }}
        />
        <TextField
          required
          name="email"
          label="Email Address"
          type="email"
          value={user.email}
          onChange={handleChange}
          fullWidth
          sx={{ mb: 2 }}
        />
        <TextField
          required
          name="password"
          label="Password"
          type="password"
          value={user.password}
          onChange={handleChange}
          fullWidth
          sx={{ mb: 2 }}
        />
        <Button variant="contained" 
          type="submit" 
          sx={{ 
            mb: 2, 
            width: '40%', 
          }}
        >
          Sign Up
        </Button>
        <Typography>
          Already have an account?{' '}
          <Link component={RouterLink} to="/signin">
            Sign in
          </Link>
        </Typography>
      </Box>
    </Box>
  );
}

export default SignUp;
