import React, { useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button'; // Import Button from MUI
import '../CSS/SignIn.css'; // Assuming you have additional styles that don't conflict with MUI

function SignIn() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('User Email:', email);
    console.log('Password:', password);
    // Here you would usually send the data to the server for verification
  };

  return (
    <div className="signin-container">
      <Box
        component="form"
        onSubmit={handleSubmit}
        noValidate
        autoComplete="off"
        sx={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          '& .MuiTextField-root': { m: 1, width: '25ch' },
        }}
      >
        <h2>Sign In</h2>
        <TextField
          required
          id="outlined-email"
          label="Email Address"
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          variant="outlined"
        />
        <TextField
          required
          id="outlined-password"
          label="Password"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          variant="outlined"
          autoComplete="current-password"
        />
        <Button 
          type="submit" 
          variant="contained" 
          sx={{ mt: 2, mb: 2, width: '25ch' }} // Matching the width with TextField
        >
          Sign In
        </Button>
      </Box>
    </div>
  );
}

export default SignIn;
